import React, {useState, useEffect} from 'react'
import { Modal, Button } from "react-bootstrap";
import axios from 'axios';
import Swal from "sweetalert2";

import Footer  from "./../../layouts/admin/Footer";
import Navbar  from "./../../layouts/admin/Navbar";
import Sidebar  from "./../../layouts/admin/Sidebar";
import Navigation from '../../layouts/admin/Navigation';

function Customers() {

    // const [loading, setLoading] = useState(true); //Loading
    const [customers, setCustomers] = useState([]); // fetch customers
    const [pagination, setPagination] = useState({});// Phân trang
    const [titleForm, setTitleForm] = useState('Thêm mới customer') //Title form
    const [checkedStatus, setCheckedStatus] = useState(false) //Check status user form
    const [customer, setCustomer] = useState({
        name:  '',
        email:  '',
        password: '',
        password_confirm: '',
        error_list: []
    }); //form

    var numPage; // Số trang hiện tại
    const callBackChildren = (num) => {
        numPage = num
        console.log(numPage)
        loadPage(numPage)
    }

    const loadPage = (numPage) => {
        axios.get(`/api/customers?page=${numPage}`).then(res => {
            if(res.status === 200){
                setCustomers(res.data.customers.data)
                setPagination({
                    current_page: res.data.customers.current_page,
                    last_page: res.data.customers.last_page,
                    to: res.data.customers.to,
                    total: res.data.customers.total,
                    from: res.data.customers.from
                })
            }
            // else{
            //     setLoading = false;
            // }
        }); 
    }

    useEffect(() =>{
            
        loadPage(numPage);

    }, []);

    var tableHTML = 
        customers.map((customer, idx) => {
            return (
                <tr key={idx}>
                    <td>{customer.customer_id}</td>
                    <td>{customer.customer_name}</td>
                    <td>{customer.email}</td>
                    <td>{customer.tel_num}</td>
                    <td>{customer.address}</td>
                    <td className="text-center">
                        <span className='icon_btn'>
                            <i className="fa-solid fa-pencil"></i>
                        </span>
                    </td>
                </tr>
            );
        })

    const handleInput = (e) => {
        setCustomer({ ...customer, [e.target.name]: e.target.value })
    }

    const resetInput = () => {
        setCustomer({
            name:  '',
            email:  '',
            password: '',
            password_confirm: '',
            error_list: [] 
        })
        // document.getElementById('USER_FORM').reset()
    }


    const [show, setShow] = useState(false);

    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);

    const submitCustomer = (e) => {

        e.preventDefault()
        const data = {
            email: customer.email,
            name: customer.name,
            password: customer.password,
            password_confirm: customer.password_confirm,
            is_active: checkedStatus,
        }

        axios.post(`/api/customer`, data).then(res => {
            if(res.data.status === 200){
                setShow(true)
                Swal.fire('Thêm mới', res.data.customer, 'success')
                loadPage(numPage)
                resetInput()
                setShow(false)
                
            }
            else if(res.data.status === 401){
                Swal.fire('Thêm mới', res.data.message, 'success')
                loadPage(numPage)
            }
            else{
                setCustomer({...customer, error_list: res.data.validation_errors})
            }
        }); 

    }

    return (        
    <div className="sb-nav-fixed">
        <form>
            <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                <Modal.Title>{titleForm}</Modal.Title>
                </Modal.Header>
                <Modal.Body>

                    <table className="w-100">
                        <tr>
                            <th scope="col">Tên</th>
                            <th scope="col">
                                <input type="text" name="name" className="form-control" value={customer.name} onChange={handleInput} placeholder="Nhập họ tên" />
                                <span className="text-alert">{customer.error_list.name}</span>
                            </th>
                        </tr>
                        <tr>
                            <th scope="row">Email</th>
                            <th scope="col">
                                <input type="email" name="email" className="form-control" value={customer.email} onChange={handleInput} placeholder="Nhập email" />
                                <span className="text-alert">{customer.error_list.email}</span>
                            </th>
                        </tr>
                        <tr>
                            <th scope="row">Mật khẩu</th>
                            <th scope="col">
                                <input type="password" name="password" className="form-control" value={customer.password} onChange={handleInput} placeholder="Nhập mật khẩu" />
                                <span className="text-alert">{customer.error_list.passsword}</span>
                            </th>
                        </tr>
                        <tr>
                            <th scope="row">Xác nhận</th>
                            <th scope="col">
                                <input type="password" name="password_confirm" value={customer.password_confirm} className="form-control" onChange={handleInput} placeholder="Xác nhận nhận mật khẩu" />
                                <span className="text-alert">{customer.error_list.password_confirm}</span>
                            </th>
                        </tr>
                        <tr>
                            <th scope="row">Trạng thái</th>
                            <th scope="col">
                            <input className="form-check-input" name="is_active" checked={checkedStatus} onClick={setCheckedStatus} type="checkbox"  />
                            </th>
                        </tr>
                    </table>
                </Modal.Body>
                <Modal.Footer>
                <Button variant="secondary" onClick={handleClose}>
                    Đóng
                </Button>
                <Button variant="primary" onClick={submitCustomer}>
                    Lưu
                </Button>
                </Modal.Footer>
            </Modal>
        </form>

            <Navbar />

                <div id="layoutSidenav">

                <Sidebar />
            
                    <div id="layoutSidenav_content">
                        <main>
                            <div className="container-fluid px-4"> 


                                <div className="card mb-4  mt-4">
                                    <div className="card-header">
                                        <span>
                                        <i className="fas fa-table me-1"></i>
                                        Danh sách khách hàng
                                        </span>
                                        <Button className="btn-add" onClick={handleShow}><i className="fa-solid fa-plus"></i> Thêm mới</Button>
                                    </div>
                                    <div className="card-body">
                                        <div className="table-responsive">
                                            <table className="table">
                                                <thead>
                                                    <tr>
                                                        <th>#</th>
                                                        <th>Họ tên</th>
                                                        <th>Email</th>
                                                        <th>Điện thoại</th>
                                                        <th>Địa chỉ</th>
                                                        <th className="text-center">Thao tác</th>
                                                    </tr>
                                                </thead>
                                                <tbody>
                                                    {/* {tableHTML} */}
                                                    {
                                                        customers.map((customer, idx) => {
                                                            return (
                                                            <tr key={idx}>
                                                                <td>{customer.customer_id}</td>
                                                                <td>{customer.customer_name}</td>
                                                                <td>{customer.email}</td>
                                                                <td>{customer.tel_num}</td>
                                                                <td>{customer.address}</td>
                                                                <td className="text-center">
                                                                    <span className='icon_btn'>
                                                                        <i className="fa-solid fa-pencil"></i>
                                                                    </span>
                                                                </td>
                                                            </tr>
                                                            );
                                                        })
                                                    }
                                                </tbody>
                                            </table>
                                            <Navigation Paginate={pagination}  childToParent={callBackChildren}/>
                                        </div>
                                    </div>
                                </div> 
                                
                            </div>
                        </main>

                        <Footer />
                    </div>
            </div>
        </div>
    );
}

export default Customers;
